function calculate_derivative(coefficients):
	// Initialize an empty list for the derivative coefficients
	derivative_coefficients = []

	// Iterate through the coefficients of the polynomial in reverse order
	for i from len(coefficients) - 1 to 1:
		// Calculate the derivative coefficient for the current term
		derivative_coefficient = i * coefficients[i]

		// Append the derivative coefficient to the result
		derivative_coefficients.append(derivative_coefficient)

	// Return the list of derivative coefficients
	return derivative_coefficients
